", TCLAE_VERSION_STRING, $$YEAR)};/* * The -16397 string will be displayed by Finder when a user * tries to open the shared library. The string should * give the user a little detail about the library's capabilities * and enough information to install the library in the correct location.   * A similar string should be placed in all shared libraries. */resource 'STR ' (-16397, purgeable) {	"Tcl® Library\n\n"	"This library provides the ability to use the AppleEvent "	"Manager from Tcl/Tk programs.  To work properly, it "	"should be placed in one of the $tcl_library paths, such as "	"in the :Tool Command Language: folder "	"within the :Extensions: folder."};/*  * We now load the TclAE library into the resource fork of the library. * There is no define for CFM68K in CodeWarrior (so far as I can tell) * so you will have to change this by hand if you have different names * for the CFM & PPC versions of the library. */data 'TEXT' (4000, "pkgIndex", purgeable, preload) {	"# TclAE package index file, version " TCLAE_VERSION "\n"	"package ifneeded " TCLAE_NAME " " TCLAE_BASIC_VERSION 	" \"load [list [file join $dir " TCLAE_FILENAME "[info sharedlibextension]]] " TCLAE_NAME "\n" /* das 25/10/00: Carbonization */	"source -rsrc tclAE_aeteIndex\""};read 'TEXT' (TCLAE_LIBRARY_RESOURCES    , "aelegacy.tcl", purgeable)  ":Tcl:aelegacy.tcl";read 'TEXT' (TCLAE_LIBRARY_RESOURCES + 1, "aebuild.tcl", purgeable) ":Tcl:aebuild.tcl";read 'TEXT' (TCLAE_LIBRARY_RESOURCES + 2, "oserrors.tcl", purgeable)    ":Tcl:oserrors.tcl";read 'TEXT' (TCLAE_LIBRARY_RESOURCES + 3, "aete.tcl", purgeable)    ":Tcl:aete.tcl";read 'TEXT' (TCLAE_LIBRARY_RESOURCES + 1000, "tclAE_aeteIndex", purgeable) ":Tcl:tclIndex";